import React, { Component } from 'react';
import {BrowserRouter, Route, Switch} from 'react-router-dom'

import Nosotros from './Nosotros'
import Contacto from './Contacto'
import Error from './Error'
import Productos from './Productos';
import dataProductos from '../data/data.json'

import ProductoShowcase from './ProductoShowcase'
import Header from './Header';

import Nav from './Nav'




class Router extends Component {

    state = { 
        productos : [],
        busqueda : ''
    }

    componentWillMount() {
        this.setState({
          productos : dataProductos
        })
    }

    unaBusqueda = (termino) => {
        this.setState({
            busqueda:termino
        })
    }

    render() { 
        return ( 
            <BrowserRouter>

                <Header/>

                <Nav/>

                <Switch>
                    <Route exact path="/" render={ () => (
                        <Productos
                            productos = {this.state.productos}
                            busqueda = {"this.unaBusqueda"}
                        />
                    )}/>
                    <Route exact path="/contacto" component={Contacto}/>

                    <Route exact path="/nosotros" component={Nosotros}/>

                    <Route exact path="/productos" render={ () => (
                        <Productos
                            productos = {this.state.productos}
                            busqueda = {this.unaBusqueda}
                        />
                    )}/>

                    <Route exact path="/producto/:productoId" render={ (props) => {

                        const idProd = props.match.params.productoId

                        return (<ProductoShowcase
                            data={this.state.productos[idProd]}
                        />)
                    }}
                    />


                    <Route component={Error}/>
                </Switch>

            </BrowserRouter>
         );
    }
}

export default Router;
